//HTML tags with JSX
"use strict";

React.render(React.createElement(
	"p",
	null,
	"This HTML element is generated by React"
), document.getElementById('container'));

//React components with JSX
//React components are functions that take props and states, and render html
var HelloReact = React.createClass({
	displayName: "HelloReact",

	render: function render() {
		return React.createElement(
			"p",
			null,
			"Hello, ",
			React.createElement("input", { type: "text", placeholder: "Your name here" }),
			"!",
			React.createElement(
				"p",
				null,
				" It is ",
				this.props.date.toTimeString()
			)
		);
	}
});

setInterval(function () {

	var MyElement = React.createElement(HelloReact, { date: new Date() });

	//what component and where
	React.render(MyElement, document.getElementById('container2'));
}, 500);

// React props demo
//use this.props to evaluate the vars passed in while rendering
var Profile = React.createClass({
	displayName: "Profile",

	render: function render() {
		return React.createElement(
			"div",
			null,
			React.createElement(
				"p",
				null,
				"I am ",
				this.props.name,
				", I study at ",
				this.props.school
			)
		);
	}
});

React.render(React.createElement(Profile, { name: "John", school: "SFSU" }), document.getElementById('profile'));

// React children demo
var MenuItem = React.createClass({
	displayName: "MenuItem",

	//this is the React components event, we will call this whem link is clicked
	printDescription: function printDescription() {

		alert(this.props.children);
	},

	render: function render() {

		return React.createElement(
			"div",
			null,
			React.createElement(
				"h3",
				null,
				this.props.title
			),
			React.createElement(
				"a",
				{ href: "#", onClick: this.printDescription },
				"Whats this?"
			),
			React.createElement(
				"p",
				null,
				this.props.children
			)
		);
	}
});

var menu = React.createElement(
	"div",
	null,
	React.createElement(
		MenuItem,
		{ title: "Cheesburger" },
		"A juicy 100% beef patty simply seasoned with a pinch of salt and pepper, melty American cheese, tangy pickles, minced onions, ketchup and mustard."
	),
	React.createElement(
		MenuItem,
		{ title: "Grilled Chicken Sandwich" },
		"100% grilled chicken breast filet seasoned to perfection with ingredients like salt, garlic and parsley â€“ seared in our kitchens, no preservatives added. Crisp leaf lettuce, fresh tomato, and a vinaigrette dressing. All atop our delectable artisan roll."
	)
);

React.render(menu, document.getElementById('menu'));

//React dynamic UI and reactivity

var LikeButton = React.createClass({
	displayName: "LikeButton",

	getInitialState: function getInitialState() {
		return { liked: false };
	},
	handleClick: function handleClick() {
		this.setState({ liked: !this.state.liked });
	},

	render: function render() {
		var text = this.state.liked ? 'like' : 'haven\'t liked';

		return React.createElement(
			"p",
			null,
			React.createElement("i", { className: "fa fa-thumbs-o-up", onClick: this.handleClick }),
			"You ",
			text,
			" this"
		);
	}
});

React.render(React.createElement(LikeButton, null), document.getElementById('likeItem'));